import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import TaskList from '../components/Tasks/TaskList';
import TaskForm from '../components/Tasks/TaskForm';
import TaskFilter from '../components/Filters/TaskFilter';
import './Tasks.css';

const Tasks = () => {
    const [tasks, setTasks] = useState([]);
    const [filteredTasks, setFilteredTasks] = useState([]);
    const [editingTask, setEditingTask] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // État pour la pagination
    const [pagination, setPagination] = useState({
        currentPage: 1,
        lastPage: 1,
        perPage: 6,
        total: 0
    });

    // État pour les filtres
    const [filters, setFilters] = useState({
        status: '',
        category: ''
    });

    const API_URL = 'http://127.0.0.1:8000/api';

    const fetchTasks = async (page = 1, filters = {}) => {
        try {
            setLoading(true);
            const token = localStorage.getItem('token');
            const params = new URLSearchParams({
                page,
                per_page: pagination.perPage,
                ...filters
            }).toString();

            const response = await axios.get(`${API_URL}/tasks?${params}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });

            if (response.status !== 200) {
                throw new Error('Failed to fetch tasks');
            }

            const { data, current_page, last_page, per_page, total } = response.data;

            setTasks(data);
            setFilteredTasks(data);
            setPagination({
                currentPage: current_page,
                lastPage: last_page,
                perPage: per_page,
                total: total
            });
        } catch (err) {
            console.error('Erreur lors de la récupération des tâches:', err);
            setError('Impossible de charger les tâches. Veuillez réessayer.');
            if (err.response?.status === 401) {
                // Rediriger vers la page de connexion si non authentifié
                window.location.href = '/login';
            }
        } finally {
            setLoading(false);
        }
    };

    // Chargement initial des tâches
    useEffect(() => {
        fetchTasks(1, filters);
    }, [filters]);

    const handleAddOrUpdateTask = async (taskData) => {
        try {
            const token = localStorage.getItem('token');
            let response;

            if (editingTask) {
                // Mise à jour d'une tâche existante
                response = await axios.put(`${API_URL}/tasks/${editingTask.id}`, taskData, {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    }
                });

                if (response.status === 200) {
                    // Recharger les données depuis le serveur pour s'assurer d'avoir les dernières mises à jour
                    fetchTasks(pagination.currentPage, filters);
                    setEditingTask(null);
                    toast.success('Tâche mise à jour avec succès !');
                }
            } else {
                // Création d'une nouvelle tâche
                response = await axios.post(`${API_URL}/tasks`, taskData, {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    }
                });

                if (response.status === 201) {
                    // Recharger la première page pour voir la nouvelle tâche
                    fetchTasks(1, filters);
                    toast.success('Tâche ajoutée avec succès !');
                }
            }
        } catch (error) {
            console.error('Erreur lors de l\'opération sur la tâche:', error);
            toast.error(`Erreur lors de ${editingTask ? 'la mise à jour' : 'l\'ajout'} de la tâche`);
        }
    };

    const handleUpdateTask = async (updatedTask) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.put(
                `${API_URL}/tasks/${updatedTask.id}`, 
                updatedTask,
                {
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Accept': 'application/json',
                        'Content-Type': 'application/json'
                    }
                }
            );

            if (response.status === 200) {
                // Recharger les données depuis le serveur
                fetchTasks(pagination.currentPage, filters);
                toast.success('Tâche mise à jour avec succès !');
            }
        } catch (error) {
            console.error('Erreur lors de la mise à jour de la tâche:', error);
            toast.error('Erreur lors de la mise à jour de la tâche');
        }
    };

    // Gestion du changement de page
    const handlePageChange = (newPage) => {
        if (newPage >= 1 && newPage <= pagination.lastPage) {
            fetchTasks(newPage, filters);
        }
    };

    // Gestion des filtres
    const handleFilterChange = (statusFilter, categoryFilter) => {
        const newFilters = {
            status: statusFilter || '',
            category: categoryFilter || ''
        };
        setFilters(newFilters);
        // Réinitialiser à la première page lors d'un changement de filtre
        fetchTasks(1, newFilters);
    };

    const handleDeleteTask = async (taskId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.delete(`${API_URL}/tasks/${taskId}`, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                }
            });

            if (response.status === 200) {
                // Mettre à jour les deux états de manière atomique
                const updateTasks = (prevTasks) => prevTasks.filter(task => task.id !== taskId);
                setTasks(updateTasks);
                setFilteredTasks(updateTasks);
                toast.success('Tâche supprimée avec succès !');
            }
        } catch (error) {
            console.error('Erreur lors de la suppression de la tâche:', error);
            toast.error('Erreur lors de la suppression de la tâche');
        }
    };

    // Supprimer handleFilterTasks car nous utilisons maintenant le filtrage côté serveur


    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="tasks-container">
            <header className="tasks-header">
                <h1>Gestionnaire de Tâches</h1>
                <p>Gérez efficacement vos tâches quotidiennes</p>
            </header>

            <div className="tasks-content">
                <div className="tasks-form-container">
                    <h2>{editingTask ? 'Modifier la tâche' : 'Ajouter une tâche'}</h2>
                    <TaskForm 
                        task={editingTask}
                        onTaskSubmit={handleAddOrUpdateTask}
                        onCancel={() => setEditingTask(null)}
                    />

                    <div style={{ marginTop: '2rem' }}>
                        <h3>Filtrer les tâches</h3>
                        <TaskFilter 
                            tasks={tasks} 
                            onFilterChange={handleFilterChange} 
                        />
                    </div>
                </div>

                <div className="tasks-list-container">
                    <h2>Mes Tâches ({pagination.total})</h2>
                    {loading ? (
                        <div className="text-center py-4">Chargement des tâches...</div>
                    ) : filteredTasks.length === 0 ? (
                        <p>Aucune tâche à afficher. Commencez par en ajouter une !</p>
                    ) : (
                        <TaskList 
                            tasks={filteredTasks} 
                            onTaskUpdate={handleUpdateTask} 
                            onTaskDelete={handleDeleteTask}
                            loading={loading}
                            error={error}
                        />
                    )}
                </div>
            </div>
        </div>
    );
};

export default Tasks;
