import { useState, useContext } from 'react';
import axios from 'axios';
// import { api } from '../services/api'; // Uncomment if you want to use the api instance
// import { registerUser, loginUser, logoutUser } from '../services/api'; // Uncomment if you want to use the api functions
// import { AuthContext } from '../context/AuthContext'; // Uncomment if you want to use the AuthContext
// import { useHistory } from 'react-router-dom'; // Uncomment if you want to use history for navigati 
import { AuthContext } from '../context/AuthContext';

const useAuth = () => {
    const { setAuth } = useContext(AuthContext);
    const [error, setError] = useState(null);

    const login = async (email, password) => {
        try {
            const response = await axios.post('/api/login', { email, password });
            setAuth(response.data);
            setError(null);
        } catch (err) {
            setError(err.response.data.message);
        }
    };

    const register = async (userData) => {
        try {
            const response = await axios.post('/api/register', userData);
            setAuth(response.data);
            setError(null);
        } catch (err) {
            setError(err.response.data.message);
        }
    };

    const logout = async () => {
        try {
            await axios.post('/api/logout');
            setAuth(null);
        } catch (err) {
            setError(err.response.data.message);
        }
    };

    return { login, register, logout, error };
};

export default useAuth;
