import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Importer useHistory pour la redirection
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Register.css'; // Importer le fichier CSS pour les styles

const Register = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);

        try {
            const response = await axios.post('http://127.0.0.1:8000/api/register', { 
                name, 
                email, 
                password, 
                password_confirmation: password 
            });

            toast.success('Inscription réussie ! Vous allez être redirigé...');

            // Redirection après un court délai
            setTimeout(() => {
                history.push('/login');
            }, 2000);

        } catch (error) {
            let errorMessage = "Une erreur s'est produite lors de l'inscription";

            if (error.response) {
                // Erreur du serveur avec un statut de réponse
                if (error.response.data.errors) {
                    // Récupérer le premier message d'erreur
                    const firstError = Object.values(error.response.data.errors)[0][0];
                    errorMessage = firstError || errorMessage;
                } else if (error.response.data.message) {
                    errorMessage = error.response.data.message;
                }
            }

            toast.error(errorMessage);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div className="register-container">
            <div className="register-form">
                <h2 className="text-center mb-4">Créer un compte</h2>
                <ToastContainer position="top-right" autoClose={5000} />
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="name" className="form-label">Full Name</label>
                        <input 
                            type="text" 
                            className="form-control form-control-lg" 
                            id="name" 
                            placeholder="Enter your name"
                            value={name} 
                            onChange={(e) => setName(e.target.value)} 
                            required 
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="email" className="form-label">Email Address</label>
                        <input 
                            type="email" 
                            className="form-control form-control-lg" 
                            id="email" 
                            placeholder="Enter your email"
                            value={email} 
                            onChange={(e) => setEmail(e.target.value)} 
                            required 
                        />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password</label>
                        <input 
                            type="password" 
                            className="form-control form-control-lg" 
                            id="password" 
                            placeholder="Enter your password"
                            value={password} 
                            onChange={(e) => setPassword(e.target.value)} 
                            required 
                        />
                    </div>
                    <button type="submit" className="btn btn-primary w-100">Register</button>
                </form>
                <div className="text-center mt-4">
                    <p className="text-muted">Already have an account? <a href="/login" className="text-decoration-none">Sign in</a></p>
                </div>
            </div>
        </div>
    );
};

export default Register;
