import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://127.0.0.1:8000/api/register', { name, email, password });
            alert('Registration successful!');
        } catch (err) {
            setError('Registration failed. Please try again.');
        }
    };

    return (
        <div className="container mt-5">
  <div className="row justify-content-center">
    <div className="col-md-6 col-lg-5">
      <div className="card shadow-lg border-0">
        <div className="card-body p-5">
          <h2 className="text-center mb-4 text-primary">Create Account</h2>

          {error && <div className="alert alert-danger">{error}</div>}

          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">Full Name</label>
              <input 
                type="text" 
                className="form-control form-control-lg" 
                id="name" 
                placeholder="Enter your name"
                value={name} 
                onChange={(e) => setName(e.target.value)} 
                required 
              />
            </div>

            <div className="mb-3">
              <label htmlFor="email" className="form-label">Email Address</label>
              <input 
                type="email" 
                className="form-control form-control-lg" 
                id="email" 
                placeholder="Enter your email"
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
                required 
              />
            </div>

            <div className="mb-4">
              <label htmlFor="password" className="form-label">Password</label>
              <input 
                type="password" 
                className="form-control form-control-lg" 
                id="password" 
                placeholder="Create a password"
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
                required 
              />
            </div>

            <button 
              type="submit" 
              className="btn btn-primary btn-lg w-100 py-2"
            >
              Register
            </button>
          </form>

          <div className="text-center mt-4">
            <p className="text-muted">Already have an account? <a href="/login" className="text-decoration-none">Sign in</a></p>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
    );
};

export default Register;










