import React, { useEffect, useState } from 'react';
import axios from 'axios';
import TaskList from '../components/Tasks/TaskList';
import TaskForm from '../components/Tasks/TaskForm';
import TaskFilter from '../components/Filters/TaskFilter';
import './Tasks.css';

const Tasks = () => {
    const [tasks, setTasks] = useState([]);
    const [filteredTasks, setFilteredTasks] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await axios.get('http://127.0.0.1:8000/api/tasks');
                if (response.status !== 200) {
                    throw new Error('Failed to fetch tasks');
                }
                setTasks(response.data);
                setFilteredTasks(response.data);
            } catch (err) {
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchTasks();
    }, []);

    const handleAddTask = (newTask) => {
        setTasks([...tasks, newTask]);
        setFilteredTasks([...tasks, newTask]);
    };

    const handleUpdateTask = (updatedTask) => {
        const updatedTasks = tasks.map(task => 
            task.id === updatedTask.id ? updatedTask : task
        );
        setTasks(updatedTasks);
        setFilteredTasks(updatedTasks);
    };

    const handleDeleteTask = (taskId) => {
        const updatedTasks = tasks.filter(task => task.id !== taskId);
        setTasks(updatedTasks);
        setFilteredTasks(updatedTasks);
    };

    const handleFilterTasks = (filtered) => {
        setFilteredTasks(filtered);
    };

    if (loading) return <div>Loading...</div>;
    if (error) return <div>Error: {error}</div>;

    return (
        <div className="tasks-container">
            <header className="tasks-header">
                <h1>Gestionnaire de Tâches</h1>
                <p>Gérez efficacement vos tâches quotidiennes</p>
            </header>

            <div className="tasks-content">
                <div className="tasks-form-container">
                    <h2>Ajouter une tâche</h2>
                    <TaskForm 
                        onTaskSubmit={handleAddTask} 
                        onCancel={() => {}} 
                    />

                    <div style={{ marginTop: '2rem' }}>
                        <h3>Filtrer les tâches</h3>
                        <TaskFilter tasks={tasks} onFilterTasks={handleFilterTasks} />
                    </div>
                </div>

                <div className="tasks-list-container">
                    <h2>Mes Tâches ({filteredTasks.length})</h2>
                    {filteredTasks.length === 0 ? (
                        <p>Aucune tâche à afficher. Commencez par en ajouter une !</p>
                    ) : (
                        <TaskList 
                            tasks={filteredTasks} 
                            onUpdateTask={handleUpdateTask} 
                            onDeleteTask={handleDeleteTask} 
                        />
                    )}
                </div>
            </div>
        </div>
    );
};

export default Tasks;
